import enum

class AffiliateLevel(enum.IntEnum):
    Platinum=1
    Gold=2
    Silver=3
    @staticmethod
    def get_name(level):
        if level==AffiliateLevel.Platinum.value:
            return "Platinum"
        elif level==AffiliateLevel.Gold.value:
            return "Gold"
        elif level==AffiliateLevel.Silver.value:
            return "Silver"