import re
class EmailValidation:
    def __init__(self,format={},*args,**kwargs):
        self.format=format
        self.regex = r'[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]'
    def is_valid(self,value):
        if value is not None:
            try:
                if  not re.match(self.regex,value):
                    return self.format['error']
                return None
            except:
                return self.format['error']

