class MimeValidation:
    def __init__(self,
                 error=None,
                 extension={},
                 *args,
                 **kwargs):
        self.extn=extension
        self.error=error

    def is_valid(self, value):
        try:
            keys=self.extn.keys()
            if value not in keys:
                return self.error
        except:
            return self.error

    def get_content_type(self,value):
        return self.extn[value]