from browser import window
import jquery
jq=window.jQuery
class Loader:
    def __init__(self):
        self.__loader=jq('.loading-container')
    
    def show_loader(self):
        if self.__loader.length > 0:
            self.__loader.css("display","flex")

    def close_loader(self):
        if self.__loader.length > 0:
            self.__loader.css("display","none")
